import React, {Component} from 'react'
import AddTodo from './AddTodo'
import TodoList from './TodoList'
import Footer from './Footer'

const todos = [
  {
    id: 1,
    text: "学习react",
    completed: true
  },
  {
    id: 2,
    text: "学习redux",
    completed: false
  },
  {
    id: 3,
    text: "学习react router",
    completed: false
  },
]

const filter = "all"

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      filter: 'all'
    }
  }

  render() {
    const {todos, filter} = this.props
    return (
      <div>
        <AddTodo/>
        <TodoList todos={todos}/>
        <Footer filter={filter}/>
      </div>
    )
  }

  getVisibleValue = () => {
    const currentFilter = this.state.filter
    return this.state.todos.filter(todo => {
      if (currentFilter === "active") { //正在进行中
        return !todo.completed
      } else if (currentFilte === "completed") {
        return todo.completed
      } else {
        return todo
      }
    })
  }
}

export default App
