export const getText = (state) => state.text

export const getFilter = (state) => state.filter

export const getVisibleTodos = (todos, filter) => {
	switch (filter) {
		case 'all':
			return todos
		case 'completed':
			return todos.filter(todo => todo.completed)
		case 'active':
			return todos.filter(todo => !todo.completed)
		default:
			return new Error('Unknow filter: ' + filter)
	}
}
